@testset "DES cipher tests" begin
  const DES_PLAINTEXT = UInt8[ 0x01, 0x23, 0x45, 0x67, 0x89, 0xAB, 0xCD, 0xEF ]
  const DES_R0        = Cryptography.unpack_bits(UInt8[ 0xF0, 0xAA, 0xF0, 0xAA ])

  cipher = DesCipher([ 0x13, 0x34, 0x57, 0x79, 0x9B, 0xBC, 0xDF, 0xF1 ])

  @test block_size(cipher) == 8
  @test key_size(cipher)   == 8

