  @test_throws ArgumentError DesCipher(UInt8[])
  @test_throws ArgumentError DesCipher([ 0x01, 0x02, 0x03, 0x04, 0x05, 0x06, 0x07 ])

  @test_throws ArgumentError encrypt(cipher, UInt8[])
  @test_throws ArgumentError encrypt(cipher, [ 0x05, 0x06 ])

  @test_throws ArgumentError decrypt(cipher, UInt8[])
  @test_throws ArgumentError decrypt(cipher, [ 0x05, 0x06 ])

  @test Cryptography.permutate(Cryptography.unpack_bits(DES_PLAINTEXT), Cryptography.DES_INITIAL_PERMUTATION) == UInt8[
    0x01, 0x01, 0x00, 0x00, 0x01, 0x01, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
    0x01, 0x01, 0x00, 0x00, 0x01, 0x01, 0x00, 0x00, 0x01, 0x01, 0x01, 0x01, 0x01, 0x01, 0x01, 0x01,
